var contactFormContainer=document.getElementById("contactFormContainer"),contactBtn=document.getElementById("contactBtn"),nameInput=document.getElementById("name"),emailInput=document.getElementById("email"),messageInput=document.getElementById("message"),errorMessage=document.getElementById("errorMessage"),successMessage=document.getElementById("successMessage");function setBtnState(){contactBtn.disabled=!(nameInput.value&&emailInput.value&&messageInput.value&&Ch.isEmail(emailInput.value))}function sendMail(e){contactBtn.disabled=!0,Aias.HTTP.POST("/mail/send","json",e).then(function(e){e.success?(contactFormContainer.style.display="none",successMessage.style.display="block"):errorMessage.style.display="block"}).catch(function(e){errorMessage.style.display="block"})}nameInput.addEventListener("keyup",function(){setBtnState()}),emailInput.addEventListener("keyup",function(){setBtnState()}),messageInput.addEventListener("keyup",function(){setBtnState()}),contactBtn.addEventListener("click",function(){sendMail({name:nameInput.value,email:emailInput.value,message:messageInput.value,from:"[www.feedinggood.com]"})});