var foodItems,listElt=document.getElementById("list"),querylistElt=document.getElementById("querylist"),searchInput=document.getElementById("searchInput");function getFooodItems(){Aias.HTTP.GET("http://nutrimetrics.estellepicq.com/food?fodmaps=1","json").then(e=>{foodItems=e,this.displayList(foodItems)}).catch(e=>{console.log("error",e)})}function displayList(e){e.forEach((e,t)=>{var n=document.createElement("div");n.textContent=e.name,n.setAttribute("class","item"),n.id="item_"+t;var d=e.fodmaps_category.toLowerCase().replace(/ /g,""),s=document.getElementById(d);if(!s){(s=document.createElement("div")).id=d,s.setAttribute("class","category"),listElt.append(s);var a=document.createElement("h4");a.textContent=e.fodmaps_category,s.append(a);var o=document.createElement("div");o.id=d+"_low",s.appendChild(o);var i=document.createElement("h5");i.textContent="Pauvres en FODMAPs",o.appendChild(i);var l=document.createElement("div");l.id=d+"_high",s.appendChild(l);var c=document.createElement("h5");c.textContent="Riches en FODMAPs",l.appendChild(c)}document.getElementById(d+"_"+e.fodmaps_indicator).appendChild(n)})}getFooodItems(),searchInput.addEventListener("keyup",function(){var e=searchInput.value;foodItems.forEach((t,n)=>{searchInput.value?listElt.classList.add("filtered-list"):listElt.classList.remove("filtered-list");var d=document.getElementById("item_"+n);t.name.toLowerCase().includes(e.toLowerCase())?d.classList.remove("hidden"):d.classList.contains("hidden")||d.classList.add("hidden")})});