var foodItems,listElt=document.getElementById("list"),searchInput=document.getElementById("searchInput");function getFooodItems(){Aias.HTTP.GET("http://nutrimetrics.estellepicq.com/food?fodmaps=1","json").then(e=>{foodItems=e,this.displayList(foodItems)}).catch(e=>{console.log("error",e)})}function displayList(e){e.forEach((e,t)=>{var o=document.createElement("div");o.textContent=e.name,o.classList.add("item","displayed"),o.id="item_"+t;var s=e.fodmaps_category.toLowerCase().replace(" ",""),a=document.getElementById(s);if(!a){(a=document.createElement("div")).id=s,listElt.append(a);var d=document.createElement("h4");d.textContent=e.fodmaps_category,a.append(d)}a.appendChild(o)})}getFooodItems(),searchInput.addEventListener("keyup",function(){var e=searchInput.value;foodItems.forEach((t,o)=>{var s=document.getElementById("item_"+o);t.name.toLowerCase().includes(e.toLowerCase())?s.classList.contains("displayed")||s.classList.add("displayed"):s.classList.remove("displayed")})});